mapper.insert:
INSERT INTO user
   SET uid = #{uid}
     , upw = #{upw}
     , nm = #{nm}
     , pic = #{pic}

mapper.select:
SELECT id, uid, upw, nm, pic, created_at
  FROM user
 WHERE uid = #{uid}

SELECT id, uid, upw, nm, pic, created_at
FROM user
WHERE id = #{id}

mapper.update:
UPDATE user
SET


            upw = #{upw}


            nm = #{nm}




                    pic = null


                    pic = #{pic}




WHERE id = #{id}
